<template>
  <div class="w-full rounded-lg bg-lightest my-4">
    <div class="p-3">
      <p class="text-lg font-bold">Who to follow</p>
    </div>
    <div v-if="currentProfile" v-for="profile in profiles" :key="profile.id" class="w-full flex hover:bg-lighter p-3 border-t border-lighter">
      <img 
        @click="pushToProfilePage(profile.id)" 
        :src="getAvatar(profile.id, profile.avatar_key) || 'default_profile.png'"
        class="w-12 h-12 rounded-full cursor-pointer"  
        :alt="profile.username"
      />
      <div class="hidden lg:block ml-4">
        <p @click="pushToProfilePage(profile.id)" class="text-left text-sm font-bold leading-tight cursor-pointer">{{ profile.username }}</p>
        <p class="text-left text-sm leading-tight text-dark">{{ profile.id.slice(0, 8) }}...</p>
      </div>
      <button
        v-if="!isFollowed(profile.id)"
        @click="follow(profile.id)" 
        class="ml-auto text-sm text-blue font-bold px-4 py-1 rounded-full border border-blue m-2"
      >
        Follow
      </button>
      <button
          v-if="isFollowed(profile.id)"
          @click="unfollow(profile.id)"
          class="ml-auto text-sm text-blue font-bold px-4 py-1 rounded-full border border-blue m-2"
      >
        Unfollow
      </button>
    </div>
    <button
        @click="loadMore()"
        class="p-3 w-full hover:bg-lighter text-left text-blue border-t border-lighter"
    >
      Show More
    </button>
  </div>
</template>

<script>
import {warpnetService} from "@/service/service";

export default {
  name: 'WhoToFollow',
  props: {
    currentProfile: Object,
  },
  data() {
    return {
      userProfile: undefined,
      profiles: [],
      profilesAvatars: new Map(),
    };
  },
  methods: {
    async pushToProfilePage(profileId) {
      this.$router.push({ 
        name: "Profile",
        params: {
          id: profileId
        }
      });
    },
    isFollowed(profileId) {
      return warpnetService.isFollowed(profileId)
    },
    async follow(profileId) {
      await warpnetService.followUser(profileId);
    },
    async unfollow(profileId) {
      await warpnetService.unfollowUser(profileId);
    },
    getAvatar(userId, avatarKey) {
      this.loadAvatar(userId, avatarKey)
      return this.profilesAvatars.get(userId)
    },
    async loadAvatar(userId, avatarKey) {
      const img  = await warpnetService.getImage({userId:userId, key:avatarKey})
      this.profilesAvatars.set(userId, img)
    },
    async loadMore() {
      const users = await warpnetService.getWhoToFollow(this.currentProfile.id,false)
      this.profiles = [...this.profiles, ...users];
    },
   },
  async created() {
    console.log("loading component:", this.$options.name);
    this.userProfile = warpnetService.getOwnerProfile();
    this.profiles = await warpnetService.getWhoToFollow(this.currentProfile.id, true)
  },
};
</script>